        tempo_permanencia = hora_saida - veiculo.entrada
        minutos_totais = int(tempo_permanencia.total_seconds() / 60)

        print(f"\n--- Recibo de Saída ---")
        print(f"Veículo: {veiculo.tipo} - {veiculo.id}")
        print(f"Tempo de permanência: {minutos_totais} minutos")
